public class Main {
    public static boolean isPowerOfThree(int n) {
        if (n == 1 || n == 0) {
            return false;
        }
        int pow3 = (int) Math.log(n, 3);
        return pow3 != -1 && pow3 % 1 == 0;
    }

    public static void main(String[] args) {
        System.out.println(isPowerOfThree(-1)); // false
        System.out.println(isPowerOfThree(0)); // false
        System.out.println(isPowerOfThree(1)); // true
        System.out.println(isPowerOfThree(2)); // false
        System.out.println(isPowerOfThree(3)); // true
        System.out.println(isPowerOfThree(7)); // false
        System.out.println(isPowerOfThree(8)); // true
    }
}
